mapper.select:
 	 SELECT * FROM t_user


	 	  and n_user_type=#{userType}


	 	  and
	 	  	(
    		c_user_account like CONCAT('%','${keywords}','%' ) 
    		or c_user_realname like CONCAT('%','${keywords}','%' ) 
   			 )


	 	  and c_sex=#{sex}



		      and DATE_FORMAT(t_create_time, '%Y-%m-%d %H:%i:%s') >=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d %H:%i:%s')




		      and DATE_FORMAT(t_create_time, '%Y-%m-%d %H:%i:%s') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d %H:%i:%s')



	 	  and n_auth_status=#{authStatus}



	   	   limit #{startRow},#{pageSize}

 	 SELECT count(*) FROM t_user


	 	  and n_user_type=#{userType}


	 	  and
	 	  	(
    		c_user_account like CONCAT('%','${keywords}','%' ) 
    		or c_user_realname like CONCAT('%','${keywords}','%' ) 
   			 )


	 	  and c_sex=#{sex}



		      and DATE_FORMAT(t_create_time, '%Y-%m-%d %H:%i:%s') >=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d %H:%i:%s')




		      and DATE_FORMAT(t_create_time, '%Y-%m-%d %H:%i:%s') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d %H:%i:%s')



	 	  and n_auth_status=#{authStatus}
